    private void loadHistoryActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loadHistoryActionPerformed
        String vendorId = jTextField4.getText().trim();
        String timeframeSelected = (String) date.getSelectedItem();
    
    // Clear the table before loading new data
        DefaultTableModel model = (DefaultTableModel) jTable4.getModel();
        model.setRowCount(0);

        try (BufferedReader br = new BufferedReader(new FileReader("order.txt"))) {
            String line;
            while ((line = br.readLine()) != null) {
                String[] orderDetails = line.split(",");
                if (orderDetails.length == 8) {
                    String customerId = orderDetails[0];
                    String orderId = orderDetails[1];
                    String item = orderDetails[2];
                    String price = orderDetails[3];
                    String orderType = orderDetails[4];
                    String orderDate = orderDetails[5];
                    String orderStatus = orderDetails[6];
                    String vendorID = orderDetails[7];

                    // Check if the vendor ID matches
                    if (vendorId.equals(vendorID)) {
                        // Filter based on the selected timeframe
                        if (isDateInTimeframe(orderDate, timeframeSelected)) {
                            // Add the row to the table
                            model.addRow(new Object[]{customerId, orderId, item, price, orderDate, orderStatus, vendorID});
                        }
                    }
                }
            }
        } catch (IOException e) {
            e.printStackTrace();
        }

    }//GEN-LAST:event_loadHistoryActionPerformed

