    private void backtomainActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_backtomainActionPerformed
        // Create an instance of VendorPage
        frame.getContentPane().removeAll();
        testing panel = new testing(frame);   //the panel you want to switch to
        frame.add(panel);
        frame.revalidate();
        frame.repaint();;
    }//GEN-LAST:event_backtomainActionPerformed

    private void panelActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_panelActionPerformed
        // Open VendorPanel
        frame.getContentPane().removeAll();
        vpanel panel = new vpanel(frame);   //the panel you want to switch to
        frame.add(panel);
        frame.revalidate();
        frame.repaint();
    }//GEN-LAST:event_panelActionPerformed

